import React, { useState, useEffect } from 'react';
import BookCard from '../components/BookCard';
import SearchBar from '../components/SearchBar';
import LoadingSpinner from '../components/LoadingSpinner';
import { ChevronDownIcon } from '@heroicons/react/outline';


const BookListPage = () => {
  // เพิ่ม FeaturedBooks State เพื่อให้โค้ดทำงานได้ครบถ้วน แม้ว่าตอนนี้จะยังไม่ได้ใช้งาน
  const [featuredBooks, setFeaturedBooks] = useState([]); 
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [sortBy, setSortBy] = useState('newest');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const booksPerPage = 12;

  const categories = [
    'all', 'fiction', 'non-fiction', 'science', 'history', 'art', 
    'psychology', 'business', 'technology', 'cooking'
  ];
  const [error, setError] = useState(null);

  useEffect(() => {
      const fetchBooks = async () => {
        try {
          setLoading(true);

          // เรียก API เพื่อดึงข้อมูลหนังสือ
          const response = await fetch('http://localhost:8080/api/v1/books');

          if (!response.ok) {
            // ถ้าไม่สำเร็จจะโยน Error
            throw new Error('Failed to fetch books or API is down.');
          }

          const data = await response.json();

          // ตรวจสอบว่าข้อมูลที่ได้มาเป็น Array หรือไม่
          const bookData = Array.isArray(data) ? data : []; 

          setBooks(bookData);         
          setFilteredBooks(bookData);

          // *******************************************************************
          // ** แก้ไข: ลบการใช้ตัวแปร 'selected' ที่ไม่ได้ประกาศออกไป **
          // *******************************************************************

          // ถ้าต้องการสุ่ม Featured Books (Optional): 
          // if (bookData.length > 0) {
          //    const selected = bookData.sort(() => 0.5 - Math.random()).slice(0, 3);
          //    setFeaturedBooks(selected);
          // }

          setError(null);

        } catch (err) {
          setError(err.message);
          console.error('Error fetching books:', err);

        } finally {
          setLoading(false);
        }
      };

      // เรียกใช้ฟังก์ชันดึงข้อมูล
      fetchBooks();
    }, []);

  const handleSearch = (searchTerm) => {
    const filtered = books.filter(book => 
      book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      book.author.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredBooks(filtered);
    setCurrentPage(1);
  };

  const handleCategoryFilter = (category) => {
    setSelectedCategory(category);
    if (category === 'all') {
      setFilteredBooks(books);
    } else {
      const filtered = books.filter(book => 
        book.category.toLowerCase() === category.toLowerCase()
      );
      setFilteredBooks(filtered);
    }
    setCurrentPage(1);
  };

  const handleSort = (sortValue) => {
    setSortBy(sortValue);
    const sorted = [...filteredBooks];
    switch (sortValue) {
      case 'price-low':
        sorted.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        sorted.sort((a, b) => b.price - a.price);
        break;
      case 'popular':
        // สมมติว่ามี field 'reviews'
        sorted.sort((a, b) => (b.reviews || 0) - (a.reviews || 0)); 
        break;
      case 'newest':
      default:
        // สมมติว่ามี field 'id' หรือ 'publishedDate' 
        // ใช้ ID ในการเรียงลำดับเบื้องต้น
        sorted.sort((a, b) => b.id - a.id);
    }
    setFilteredBooks(sorted);
  };

  // Pagination logic
  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;
  const currentBooks = filteredBooks.slice(indexOfFirstBook, indexOfLastBook);
  const totalPages = Math.ceil(filteredBooks.length / booksPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (loading) {
    return <LoadingSpinner />;
  }

  // แสดงข้อความ error ถ้าดึงข้อมูลไม่สำเร็จ และไม่มีหนังสือ
  if (error && filteredBooks.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center p-8 bg-white shadow-xl rounded-lg">
          <h2 className="text-xl font-bold text-red-600 mb-4">
            ⚠️ ไม่สามารถเชื่อมต่อกับ API ได้
          </h2>
          <p className="text-gray-700">
            โปรดตรวจสอบว่า Backend Server (localhost:8080) ทำงานอยู่หรือไม่
          </p>
          <p className="text-sm text-gray-500 mt-2">รายละเอียด: {error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        {/* Page Header */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">หนังสือทั้งหมด</h1>
          <p className="text-gray-600">ค้นพบหนังสือที่คุณชื่นชอบจากคอลเล็กชันของเรา</p>
        </div>

        {/* Filters and Search */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <div className="flex flex-col lg:flex-row gap-4">
            {/* Search */}
            <div className="flex-1">
              <SearchBar onSearch={handleSearch} />
            </div>

            {/* Category Filter */}
            <select 
              className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none 
                focus:ring-2 focus:ring-viridian-500 cursor-pointer"
              value={selectedCategory}
              onChange={(e) => handleCategoryFilter(e.target.value)}
            >
              <option value="all">ทุกหมวดหมู่</option>
              <option value="fiction">นิยาย</option>
              <option value="non-fiction">สารคดี</option>
              <option value="science">วิทยาศาสตร์</option>
              <option value="history">ประวัติศาสตร์</option>
              <option value="art">ศิลปะ</option>
              <option value="psychology">จิตวิทยา</option>
              <option value="business">ธุรกิจ</option>
              <option value="technology">เทคโนโลยี</option>
              <option value="cooking">อาหาร</option>
            </select>

            {/* Sort */}
            <select 
              className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none 
                focus:ring-2 focus:ring-viridian-500 cursor-pointer"
              value={sortBy}
              onChange={(e) => handleSort(e.target.value)}
            >
              <option value="newest">ใหม่ล่าสุด</option>
              <option value="price-low">ราคาต่ำ-สูง</option>
              <option value="price-high">ราคาสูง-ต่ำ</option>
              <option value="popular">ยอดนิยม</option>
            </select>
          </div>

          {/* Results count */}
          <div className="mt-4 text-sm text-gray-600">
            พบหนังสือ {filteredBooks.length} เล่ม
            {selectedCategory !== 'all' && ` ในหมวด ${selectedCategory}`}
          </div>
        </div>

        {/* Books Grid */}
        {currentBooks.length > 0 ? (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {currentBooks.map(book => (
              <BookCard key={book.id} book={book} />
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <p className="text-gray-500 text-lg">ไม่พบหนังสือที่ค้นหา</p>
            {/* แสดงข้อความเพิ่มเติม ถ้าไม่มีหนังสือทั้งหมด (API ตอบกลับมาเป็น 0) */}
            {books.length === 0 && !error && (
              <p className="text-gray-400 mt-2">ขณะนี้ไม่มีหนังสือในระบบ</p>
            )}
          </div>
        )}

        {/* Pagination */}
        {totalPages > 1 && (
          <div className="mt-12 flex justify-center">
            <nav className="flex items-center space-x-2">
              <button 
                onClick={() => paginate(currentPage - 1)}
                disabled={currentPage === 1}
                className="px-4 py-2 border border-gray-300 rounded-lg 
                  hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed">
                ก่อนหน้า
              </button>

              {[...Array(totalPages)].map((_, index) => {
                const pageNumber = index + 1;
                // Simplified pagination rendering (แสดงทุกหน้า)
                return (
                  <button 
                    key={pageNumber}
                    onClick={() => paginate(pageNumber)}
                    className={`px-4 py-2 rounded-lg ${
                      currentPage === pageNumber
                        ? 'bg-viridian-600 text-white' 
                        : 'border border-gray-300 hover:bg-gray-50'
                    }`}
                  >
                    {pageNumber}
                  </button>
                );
              })}

              <button 
                onClick={() => paginate(currentPage + 1)}
                disabled={currentPage === totalPages}
                className="px-4 py-2 border border-gray-300 rounded-lg 
                  hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed">
                ถัดไป
              </button>
            </nav>
          </div>
        )}
      </div>
    </div>
  );
};

export default BookListPage;
