import React from 'react';
import { BrowserRouter as Router, Routes, Route, Outlet, Navigate } from 'react-router-dom';

// Components
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import NotFound from './components/NotFound';

// Pages
import HomePage from './pages/HomePage';
import BookListPage from './pages/BookListPage';
import BookDetailPage from './pages/BookDetailPage';
import CategoryPage from './pages/CategoryPage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';
import LoginPage from './pages/LoginPage';
import AddBookPage from './pages/AddBookPage';

// **********************************************
// * 1. Component สำหรับ Layout ที่มี Navbar/Footer
// **********************************************
const MainLayout = () => (
  <div className="flex flex-col min-h-screen">
    <Navbar />
    <main className="flex-grow bg-gray-50">
      <Outlet /> {/* Outlet ใช้สำหรับแสดงผล Component ลูก (Routes ภายใน) */}
    </main>
    <Footer />
  </div>
);

// **********************************************
// * 2. Component สำหรับจัดการ Redirect หน้า Home (/)
// **********************************************
const ProtectedHome = () => {
  const isAuthenticated = localStorage.getItem('isAdminAuthenticated');

  if (!isAuthenticated) {
    // ถ้าไม่มี Token ให้ Redirect ไปหน้า Login ทันที (แก้ปัญหาตามที่ร้องขอ)
    return <Navigate to="/login" replace />;
  }

  // ถ้ามี Token ให้แสดงหน้าหลักของร้านค้า
  return <BookListPage />;
};

function App() {
  return (
    <Router>
      <Routes>

        {/* ********************************************** */}
        {/* * 3. Routes ที่ไม่ต้องมี Navbar/Footer */}
        {/* ********************************************** */}

        {/* หน้า Login: ไม่มี Navbar/Footer */}
        <Route path="/login" element={<LoginPage />} />

        {/* หน้า BackOffice: การป้องกันถูกจัดการภายใน AddBookPage.jsx */}
        <Route path="/store-manager/add-book" element={<AddBookPage />} /> 


        {/* ********************************************** */}
        {/* * 4. Routes ทั้งหมดที่ต้องมี Navbar/Footer (ใช้ MainLayout) */}
        {/* ********************************************** */}
        <Route path="/" element={<MainLayout />}>

          {/* Index Route: หน้าแรก (Path: /) 
             ใช้ ProtectedHome ในการตรวจสอบ Token และ Redirect
          */}
          <Route index element={<ProtectedHome />} /> 

          {/* Routes เว็บไซต์ทั่วไป (เปลี่ยน /books เดิมให้เป็น Books List Page) */}
          {/* Note: เนื่องจากเราใช้ ProtectedHome ที่ / จึงทำให้หน้า /books เดิม ถูกย้ายไปที่ /books
             แต่ถ้าคุณต้องการให้ /books แสดงโดยไม่ต้อง Login ให้เปลี่ยน index element กลับเป็น HomePage 
             และย้าย BookListPage มาที่ path="books" 

             (สำหรับโครงสร้างนี้ ถ้า Login แล้ว / จะไปที่ BookListPage เลย)
          */}
          <Route path="books" element={<BookListPage />} />
          <Route path="books/:id" element={<BookDetailPage />} />
          <Route path="categories" element={<CategoryPage />} />
          <Route path="categories/:category" element={<CategoryPage />} />
          <Route path="about" element={<AboutPage />} />
          <Route path="contact" element={<ContactPage />} />

          {/* Catch-all/404 Page สำหรับ Routes ภายใน MainLayout */}
          <Route path="*" element={<NotFound />} />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
